generator = None


def generate_answer(question):
    global generator

    question_lower = question.lower()
    for keyword, predefined in PREDEFINED_ANSWERS.items():
        if all(word in question_lower for word in keyword.split()):
            return predefined

    if generator is None:
        try:
            print("üîÅ –ó–∞–≥—Ä—É–∂–∞—é AI –º–æ–¥–µ–ª—å...")

            generator = pipeline("text-generation", model="sshleifer/tiny-gpt2")
        except Exception as e:
            return "‚ö†Ô∏è –û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –º–æ–¥–µ–ª–∏. –ü–æ–ø—Ä–æ–±—É–π—Ç–µ –ø–æ–∑–∂–µ."

    prompt = f"–í–æ–ø—Ä–æ—Å: {question}\n–û—Ç–≤–µ—Ç:"

    try:
        result = generator(
            prompt,
            max_length=60,
            do_sample=True,
            top_k=50,
            top_p=0.95,
            temperature=0.8,
            repetition_penalty=1.2,
        )[0]['generated_text']
        return result.split("–û—Ç–≤–µ—Ç:")[-1].strip()
    except Exception as e:
        return "‚ö†Ô∏è –û—à–∏–±–∫–∞ –≥–µ–Ω–µ—Ä–∞—Ü–∏–∏ –æ—Ç–≤–µ—Ç–∞. –ü–æ–ø—Ä–æ–±—É–π—Ç–µ –µ—â—ë —Ä–∞–∑ –ø–æ–∑–∂–µ."